import React from 'react';

const conteudos = [
    {
      conteudo: 'High Order Functions',
      bloco: 8,
      status: 'Aprendido'
    },
    {
      conteudo: 'Composicao de Componentes',
      bloco: 11,
      status: 'Aprendendo',
    },
    {
      conteudo: 'Composicao de Estados',
      bloco: 12,
      status: 'Aprenderei'
    },
    {
      conteudo: 'Redux',
      bloco: 16,
      status: 'Aprenderei'
    },
  ];

const conteudoList = ({conteudo, bloco, status}) => {
    return (
        conteudos.map((item) => {
            return {
                conteudo: item,
                bloco: item,
                status: item
            }
        })
    )
}

class Content extends React.Component {
    render() {
      return  conteudoList()
    }
}

export default Content
