import React from 'react';

const conteudos = [
    {
      conteudo: 'High Order Functions',
      bloco: 8,
      status: 'Aprendido'
    },
    {
      conteudo: 'Composicao de Componentes',
      bloco: 11,
      status: 'Aprendendo',
    },
    {
      conteudo: 'Composicao de Estados',
      bloco: 12,
      status: 'Aprenderei'
    },
    {
      conteudo: 'Redux',
      bloco: 16,
      status: 'Aprenderei'
    },
  ];

const criaObjeto = ({conteudo, bloco, status}) => {
    return (
        <div className="div-conteudos" key={conteudo}>
        <span className='item-obj'>
        <p className='item-obj'>O conteúdo é: {conteudo}</p>
        <p className='item-obj'>Status: {status}</p>
        <p className='item-obj'>Bloco: {bloco}</p>
        </span>
        </div>
    )
}

const objetoList = () => {
    return (
        conteudos.map(({conteudo, bloco, status}) => {
        return criaObjeto({conteudo, bloco, status})
        })
    )
}

class Content extends React.Component {
    render() {
      return  objetoList()
    }
}

export default Content
