import React from 'react';
const imagem1 = 'https://www.des1gnon.com/wp-content/uploads/2020/03/Pexels-Banco-de-Imagens-gratis-Des1gnON.jpg'
const imagem2 = 'https://aceleracaodigital.com/wp-content/uploads/2016/05/sites-imagens-gratuitas.jpg'
const imagem3 = 'https://storage.googleapis.com/dpw/app/uploads/2019/04/lazy-loading-nativo-imagens-iframes_.jpg'
const imagem4 = 'https://s1.static.brasilescola.uol.com.br/be/conteudo/images/imagem-em-lente-convexa.jpg'
const imagem5 = 'https://s2.glbimg.com/SkyLTd6VJy8WiUMg5L6EeUwgyMw=/0x0:620x548/984x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_08fbf48bc0524877943fe86e43087e7a/internal_photos/bs/2021/B/t/limPwzQmSeI4WJO7haZg/2012-08-15-mf1.jpg'

const arrayImage = [imagem1, imagem2, imagem3, imagem4, imagem5, imagem5];

// Abaixo função que cria cada uma das imagens

const criaImagem = (value) => {
    return <img className='imagens' src={value} alt=""/>
}

// Abaixo a função que cria cada uma das imagens do array

const imagemList = () => {
    return (
        arrayImage.map((imagem) => {
            return criaImagem(imagem)
        })
    )
}


class Blocks extends React.Component {
    render() {
        return (
            <div className='div-block'>
                {imagemList()}
            </div>
        )
    }
}

export default Blocks;
